import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';

const MapComponent = () => {
  const [locations, setLocations] = useState([]); // Stores fetched coordinates
  const [filteredLocations, setFilteredLocations] = useState([]);
  const [filter, setFilter] = useState('All');

  // Fetch Bitcoin shop locations from the API
  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const response = await axios.get('https://api.btcmap.org/v2/areas/btc-isla');
        const coordinates = response.data.tags.geo_json.coordinates[0]; // Extract geo_json coordinates
        // Format the data for Leaflet Markers
        const formattedLocations = coordinates.map((coord, index) => ({
          id: index,
          latitude: coord[1], // Leaflet expects latitude first
          longitude: coord[0],
          type: 'Bitcoin Shop', // Temporary label
        }));
        setLocations(formattedLocations);
        setFilteredLocations(formattedLocations);
      } catch (error) {
        console.error('Error fetching locations:', error.message);
      }
    };

    fetchLocations();
  }, []);

  // Filtering logic (if needed)
  useEffect(() => {
    if (filter === 'All') {
      setFilteredLocations(locations);
    } else {
      setFilteredLocations(locations.filter((location) => location.type === filter));
    }
  }, [filter, locations]);

  return (
    <div>
      <h1>Bitcoin Map of Isla Mujeres</h1>

      {/* Filter Dropdown */}
      <div style={{ marginBottom: '10px' }}>
        <label htmlFor="filter">Filter by Type:</label>
        <select id="filter" onChange={(e) => setFilter(e.target.value)}>
          <option value="All">All</option>
          <option value="Bitcoin Shop">Bitcoin Shop</option>
        </select>
      </div>

      {/* Map */}
      <MapContainer center={[21.2322, -86.7314]} zoom={13} style={{ height: '500px', width: '100%' }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href='http://osm.org/copyright'>OpenStreetMap</a> contributors"
        />
        {filteredLocations.map((location) => (
          <Marker key={location.id} position={[location.latitude, location.longitude]}>
            <Popup>
              <strong>Type:</strong> {location.type}
              <br />
              <strong>Latitude:</strong> {location.latitude}
              <br />
              <strong>Longitude:</strong> {location.longitude}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default MapComponent;
