import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';

const MapComponent = () => {
  const [nodes, setNodes] = useState([]); // Stores fetched nodes dynamically

  // Fetch all nodes and filter those with btc-isla area
  useEffect(() => {
    const fetchNodes = async () => {
      try {
        const response = await axios.get('https://api.btcmap.org/v2/elements');
        const allNodes = response.data;

        // Filter nodes where areas include url_alias: "btc-isla"
        const filteredNodes = allNodes.filter((node) =>
          node.tags.areas.some((area) => area.url_alias === 'btc-isla')
        );

        // Format filtered nodes to extract relevant details
        const formattedNodes = filteredNodes.map((node) => ({
          id: node.id,
          name: node.osm_json.tags.name || 'Unnamed Location',
          latitude: node.osm_json.lat,
          longitude: node.osm_json.lon,
          category: node.tags.category || 'Other',
          website: node.osm_json.tags.website || null,
          phone: node.osm_json.tags.phone || null,
        }));

        setNodes(formattedNodes);
      } catch (error) {
        console.error('Error fetching nodes:', error.message);
      }
    };

    fetchNodes();
  }, []);

  return (
    <div>
      <h1>Bitcoin Map of Isla Mujeres</h1>
      <MapContainer center={[21.2322, -86.7314]} zoom={14} style={{ height: '500px', width: '100%' }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />

        {/* Render markers dynamically */}
        {nodes.map((node) => (
          <Marker key={node.id} position={[node.latitude, node.longitude]}>
            <Popup>
              <strong>{node.name}</strong>
              <br />
              Category: {node.category}
              <br />
              {node.website && (
                <>
                  Website: <a href={node.website} target="_blank" rel="noopener noreferrer">{node.website}</a>
                  <br />
                </>
              )}
              {node.phone && `Phone: ${node.phone}`}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default MapComponent;
