import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import axios from 'axios';
import 'leaflet/dist/leaflet.css';

const App = () => {
  const [locations, setLocations] = useState([]);
  const [filteredLocations, setFilteredLocations] = useState([]);
  const [filter, setFilter] = useState('All');

  useEffect(() => {
    // Fetch locations from the backend
    const fetchLocations = async () => {
      try {
        const response = await axios.get('http://localhost:5050/locations');
        setLocations(response.data);
        setFilteredLocations(response.data);
      } catch (error) {
        console.error('Error fetching locations:', error.message);
      }
    };

    fetchLocations();
  }, []);

  // Filter locations based on selected type
  useEffect(() => {
    if (filter === 'All') {
      setFilteredLocations(locations);
    } else {
      setFilteredLocations(locations.filter(location => location.type === filter));
    }
  }, [filter, locations]);

  return (
    <div>
      <h1>Bitcoin Map of Isla Mujeres</h1>

      {/* Filter Dropdown */}
      <div style={{ marginBottom: '10px' }}>
        <label htmlFor="filter">Filter by Type:</label>
        <select id="filter" onChange={(e) => setFilter(e.target.value)}>
          <option value="All">All</option>
          <option value="Retail">Retail</option>
          <option value="Restaurant">Restaurant</option>
          <option value="Hotel">Hotel</option>
          <option value="Cafe">Cafe</option>
        </select>
      </div>

      {/* Map */}
      <MapContainer center={[21.2322, -86.7314]} zoom={13} style={{ height: '500px', width: '100%' }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href='http://osm.org/copyright'>OpenStreetMap</a> contributors"
        />
        {filteredLocations.map((location) => (
          <Marker key={location.id} position={[location.latitude, location.longitude]}>
            <Popup>
              <strong>Type:</strong> {location.type}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default App;
